import React from "react";
import PropTypes from "prop-types";

function Food({ name, image, rating }) {
  return (
    <div>
      <h2>I like {name}</h2>
      <h4>{rating}/5.0</h4>
      <img src={image} alt={name} />
    </div>
  );
}

// Food.sexyTypes 안됨. 이건 룰
Food.propTypes = {
  // 내가 얻고 싶은 props들에 대한 설명.
  name: PropTypes.string.isRequired,
  image: PropTypes.string.isRequired,
  // isRequired가 없으면 반드시 그 props를 받지 않아도 된다고 설정할 수 있다.
  // 그러나 받을거면 number 받으라고 명시했는데 string 받으면 에러남
  rating: PropTypes.number,
};

function App() {
  return (
    <div>
      {foodILike.map((dish) => (
        <Food
          key={dish.id}
          name={dish.name}
          image={dish.image}
          rating={dish.rating}
        />
      ))}
    </div>
  );
}

export default App;
